import React, { useState, useEffect } from 'react';
import { getPlayers, getPlayer, updatePlayer, deletePlayer } from '../services/playerService';
import Layout from '../components/common/Layout';

const Player = () => {
  const [players, setPlayers] = useState([]);
  const [filteredPlayers, setFilteredPlayers] = useState([]);
  const [searchId, setSearchId] = useState('');
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  // For edit functionality
  const [editingPlayer, setEditingPlayer] = useState(null);
  const [editForm, setEditForm] = useState({
    playerName: '',
    email: '',
    dateJoined: '',
    totalScore: 0,
  });

  // Load players on component mount
  useEffect(() => {
    const fetchPlayers = async () => {
      try {
        setIsLoading(true);
        const data = await getPlayers();
        setPlayers(data);
        setFilteredPlayers(data);
        setIsLoading(false);
      } catch (err) {
        setError(err.message);
        setIsLoading(false);
      }
    };

    fetchPlayers();
  }, []);

  // Handle search by player ID
  const handleSearch = () => {
    if (!searchId.trim()) {
      setFilteredPlayers(players);
      return;
    }

    const filtered = players.filter(player => 
      player.playerId.toString().includes(searchId.trim())
    );
    setFilteredPlayers(filtered);
  };

  // Handle search input changes with debounce
  const handleSearchChange = (e) => {
    setSearchId(e.target.value);
    setTimeout(() => {
      handleSearch();
    }, 300);
  };

  // Handle edit button click
  const handleEditClick = async (playerId) => {
    try {
      const playerData = await getPlayer(playerId);
      setEditingPlayer(playerId);
      setEditForm({
        playerName: playerData.playerName,
        email: playerData.email,
        dateJoined: playerData.dateJoined.split('T')[0], // Format date for input
        totalScore: playerData.totalScore,
      });
    } catch (err) {
      setError('Failed to load player data for editing');
    }
  };

  // Handle form input changes
  const handleFormChange = (e) => {
    const { name, value } = e.target;
    setEditForm({
      ...editForm,
      [name]: value,
    });
  };

  // Handle save edit
  const handleSaveEdit = async () => {
    try {
      await updatePlayer(editingPlayer, editForm);

      // Update local state
      const updatedPlayers = players.map(player => 
        player.playerId === editingPlayer 
          ? { ...player, ...editForm } 
          : player
      );

      setPlayers(updatedPlayers);
      setFilteredPlayers(updatedPlayers);
      setEditingPlayer(null);
    } catch (err) {
      setError('Failed to update player');
    }
  };

  // Handle delete player
  const handleDeletePlayer = async (playerId) => {
    if (window.confirm('Are you sure you want to delete this player? This will also remove all related sessions and achievements.')) {
      try {
        await deletePlayer(playerId);

        // Update local state
        const updatedPlayers = players.filter(player => player.playerId !== playerId);
        setPlayers(updatedPlayers);
        setFilteredPlayers(updatedPlayers);
      } catch (err) {
        setError('Failed to delete player');
      }
    }
  };

  // Cancel editing
  const handleCancelEdit = () => {
    setEditingPlayer(null);
  };

  if (isLoading) return (
    <Layout>
      <div className="flex justify-center items-center h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
      </div>
    </Layout>
  );

  if (error) return (
    <Layout>
      <div className="text-center p-8">
        <p className="text-red-500 text-xl">Error: {error}</p>
        <button 
          onClick={() => window.location.reload()} 
          className="mt-4 bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded"
        >
          Retry
        </button>
      </div>
    </Layout>
  );

  return (
    <Layout>
      <div className="container mx-auto p-4">
        <h1 className="text-2xl font-bold mb-6">Player Management</h1>

        {/* Search Bar */}
        <div className="mb-6 flex">
          <input
            type="text"
            placeholder="Search by Player ID"
            value={searchId}
            onChange={handleSearchChange}
            className="px-4 py-2 border rounded-l focus:outline-none focus:ring-2 focus:ring-blue-500 flex-grow"
          />
          <button
            onClick={handleSearch}
            className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded-r"
          >
            Search
          </button>
        </div>

        {/* Player Table */}
        <div className="overflow-x-auto">
          <table className="min-w-full bg-white border border-gray-200 rounded shadow">
            <thead className="bg-gray-100">
              <tr>
                <th className="py-3 px-4 text-left">ID</th>
                <th className="py-3 px-4 text-left">Name</th>
                <th className="py-3 px-4 text-left">Email</th>
                <th className="py-3 px-4 text-left">Date Joined</th>
                <th className="py-3 px-4 text-left">Total Score</th>
                <th className="py-3 px-4 text-left">Actions</th>
              </tr>
            </thead>
            <tbody>
              {filteredPlayers.length > 0 ? (
                filteredPlayers.map(player => (
                  <tr key={player.playerId} className="border-t hover:bg-gray-50">
                    {editingPlayer === player.playerId ? (
                      // Edit mode
                      <>
                        <td className="py-3 px-4">{player.playerId}</td>
                        <td className="py-3 px-4">
                          <input
                            type="text"
                            name="playerName"
                            value={editForm.playerName}
                            onChange={handleFormChange}
                            className="w-full px-2 py-1 border rounded"
                          />
                        </td>
                        <td className="py-3 px-4">
                          <input
                            type="email"
                            name="email"
                            value={editForm.email}
                            onChange={handleFormChange}
                            className="w-full px-2 py-1 border rounded"
                          />
                        </td>
                        <td className="py-3 px-4">
                          <input
                            type="date"
                            name="dateJoined"
                            value={editForm.dateJoined}
                            onChange={handleFormChange}
                            className="w-full px-2 py-1 border rounded"
                          />
                        </td>
                        <td className="py-3 px-4">
                          <input
                            type="number"
                            name="totalScore"
                            value={editForm.totalScore}
                            onChange={handleFormChange}
                            className="w-full px-2 py-1 border rounded"
                          />
                        </td>
                        <td className="py-3 px-4">
                          <button
                            onClick={handleSaveEdit}
                            className="mr-2 bg-green-500 hover:bg-green-600 text-white px-3 py-1 rounded text-sm"
                          >
                            Save
                          </button>
                          <button
                            onClick={handleCancelEdit}
                            className="bg-gray-500 hover:bg-gray-600 text-white px-3 py-1 rounded text-sm"
                          >
                            Cancel
                          </button>
                        </td>
                      </>
                    ) : (
                      // View mode
                      <>
                        <td className="py-3 px-4">{player.playerId}</td>
                        <td className="py-3 px-4">{player.playerName}</td>
                        <td className="py-3 px-4">{player.email}</td>
                        <td className="py-3 px-4">{new Date(player.dateJoined).toLocaleDateString()}</td>
                        <td className="py-3 px-4">{player.totalScore}</td>
                        <td className="py-3 px-4">
                          <button
                            onClick={() => handleEditClick(player.playerId)}
                            className="mr-2 bg-blue-500 hover:bg-blue-600 text-white px-3 py-1 rounded text-sm"
                          >
                            Edit
                          </button>
                          <button
                            onClick={() => handleDeletePlayer(player.playerId)}
                            className="bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded text-sm"
                          >
                            Delete
                          </button>
                        </td>
                      </>
                    )}
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="6" className="py-4 px-4 text-center text-gray-500">
                    No players found
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </Layout>
  );
};

export default Player;
