export const getAveragePlayTimePerPlayer = async () => {
  const res = await fetch('http://localhost/dbms-practicum1/api/analytics/playtime.php?type=average');
  const text = await res.text();
  console.log("Average Playtime Raw:", text);

  try {
    return JSON.parse(text);
  } catch (err) {
    throw new Error("Invalid JSON from average playtime");
  }
};

export const getWeeklyPlayTimeByPlayer = async () => {
  const res = await fetch('http://localhost/dbms-practicum1/api/analytics/playtime.php?type=total');
  const text = await res.text();
  console.log("Weekly Playtime Raw:", text);

  try {
    return JSON.parse(text);
  } catch (err) {
    throw new Error("Invalid JSON from weekly playtime");
  }
};
